import React, { Component } from 'react';

//const knex = require('knex')(require('./server/db/knexfile'));

class CarList extends Component {

	constructor(props) {
		super(props);
		this.state = {
			cars: this.getCars()
		};
		this.sortCars([this.props.latitude, this.props.longitude], this.state.cars);
	}

	/**	Gets the distance between two points (lat1, lon1) & (lat2, long2)
	*	and returns it in metres
	*	Function uses the Haversine distance
	*	Implementation adapted from GeoSourceData.com's function in Javascript
	**/
	getDistance(lat1, lon1, lat2, lon2) {
		const SEMI_CIRCLE_DEGREES = 180;
	    var radlat1 = Math.PI * lat1/SEMI_CIRCLE_DEGREES;
	    var radlat2 = Math.PI * lat2/SEMI_CIRCLE_DEGREES;
	    var radlon1 = Math.PI * lon1/SEMI_CIRCLE_DEGREES;
	    var radlon2 = Math.PI * lon2/SEMI_CIRCLE_DEGREES;
	    var theta = lon1-lon2;
	    var radtheta = Math.PI * theta/SEMI_CIRCLE_DEGREES;
	    var dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
	    dist = Math.acos(dist);
	    dist = dist * SEMI_CIRCLE_DEGREES/Math.PI;
	    dist = dist * (SEMI_CIRCLE_DEGREES / 2) * 1.1515;
	    dist = dist * 1.609344;
	    return dist
	}

	sortCars(userposition, cars) {
		var userlat = userposition.coords.latitude;
		var userlong = userposition.coords.longitude;

		this.state.cars.sort(function(a,b) {
			if (this.getDistance(userlat, userlong, a.latitude, a.longitude) <
			 this.getDistance(userlat, userlong, b.latitude, b.longitude)) {
				return -1;
			} else {
				return 1;
			};
		});
	}

	getCars() {
		//return knex.select().from('cars');
		var cars = [
			{ rego: '123456', make: 'Car1', latitude: '147', longitude: '31'},
			{ rego: '132456', make: 'Car2', latitude: '144', longitude: '33'},
			{ rego: '154326', make: 'Car3', latitude: '143', longitude: '34'},
			{ rego: '543321', make: 'Car4', latitude: '145', longitude: '32'}
		];

		return cars;
	}



	render(props) {
		const carlist = this.state.cars.map((car, rego)=>
				<li key={car.rego}>{car.make}</li>
				);
		return (

			<div classname="CarList">
				<ul>
					{carlist}
				</ul>
			</div>
		);
	}
}

export default CarList;
